Analytics.Script:
CREATE TEMPORARY TABLE activeSessionTable USING CarbonAnalytics OPTIONS (tableName "ORG_WSO2_IS_ANALYTICS_STREAM_ACTIVESESSIONS", schema "meta_tenantId INT -i -f, sessionId STRING -i -f, startTimestamp LONG -i, renewTimestamp LONG -i, terminationTimestamp LONG -i, year INT, month INT, day INT, hour INT, minute INT, action INT -i -f, userName STRING -i -f, userstoreDomain STRING -i -f, IP STRING -i -f, region STRING -i -f, tenantDomain STRING -i -f, serviceProvider STRING -i -f, identityProviders STRING -i -f, rememberMeFlag BOOLEAN, userAgent STRING -i -f, _timestamp LONG -i", primaryKeys "meta_tenantId, sessionId", mergeSchema "false");

CREATE TEMPORARY TABLE rawSessionsTable USING CarbonAnalytics OPTIONS (tableName "ORG_WSO2_IS_ANALYTICS_STREAM_SESSIONINFO", schema "meta_tenantId INT -i -f, sessionId STRING -i -f, startTimestamp LONG -i, renewTimestamp LONG -i, terminationTimestamp LONG -i, endTimestamp LONG -i, year INT, month INT, day INT, hour INT, minute INT, duration LONG -f -sp, isActive BOOLEAN -i -f, userName STRING -i -f, userstoreDomain STRING -i -f, IP STRING -i -f, tenantDomain STRING -i -f, serviceProvider STRING -i -f, identityProviders STRING -i -f, rememberMeFlag BOOLEAN -i -f, _timestamp LONG -i", primaryKeys "meta_tenantId, sessionId", mergeSchema "false");

INSERT INTO TABLE rawSessionsTable SELECT meta_tenantId, sessionId, startTimestamp, renewTimestamp, terminationTimestamp, -1, year, month, day, hour, minute, now(0) - startTimestamp, true, userName, userstoreDomain, IP, tenantDomain, serviceProvider, identityProviders, rememberMeFlag, -1 from activeSessionTable where terminationTimestamp > now(0);

CREATE TEMPORARY TABLE sesssionEventsNotificationTable USING org.wso2.carbon.analytics.spark.event.EventStreamProvider OPTIONS (streamName "org.wso2.is.analytics.stream.OverallSession", version "1.0.0", payload "meta_tenantId INT, sessionId STRING, startTimestamp LONG, renewTimestamp LONG, terminationTimestamp LONG, action INT, userName STRING, userstoreDomain STRING, IP STRING, region STRING, tenantDomain STRING, serviceProvider STRING, identityProviders STRING, rememberMeFlag BOOLEAN, userAgent STRING, _timestamp LONG");

INSERT INTO TABLE sesssionEventsNotificationTable SELECT meta_tenantId, sessionId, startTimestamp, renewTimestamp, terminationTimestamp, 0, userName, userstoreDomain, IP, region, tenantDomain, serviceProvider, identityProviders, rememberMeFlag, userAgent, now(0) from activeSessionTable WHERE terminationTimestamp < now(0);
